import React, { useState, useEffect } from 'react';
import { Joke } from '../../backend/src/types';
import { JokeCard } from './components/joke_card/joke_card';
import { getJoke, saveJoke } from './services/joke_service';
import './App.css';
function App() {
  const [currentJoke, setCurrentJoke] = useState<Joke | null>(null);
  const [searchTerm, setSearchTerm] = useState('');
  const [error, setError] = useState<string | null>(null);
  const fetchNewJoke = async (search?: string) => {
    try {
      setError(null);
      const joke = await getJoke(search);
      setCurrentJoke(joke);
    } catch (err) {
      setError('Error al obtener el chiste');
    }
  };
  const handleSaveJoke = async (joke: Joke) => {
    try {
      await saveJoke(joke);
      alert('¡Chiste guardado con éxito!');
    } catch (err) {
      alert('Error al guardar el chiste');
    }
  };
  useEffect(() => {
    fetchNewJoke();
  }, []);
  return (
    <div className="App">
      <h1>Generador de Chistes</h1>

      <div className="search-container">
        <input
          type="text"
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
          placeholder="Buscar chiste..."
        />
        <button onClick={() => fetchNewJoke(searchTerm)}>
          Buscar
        </button>
      </div>
      {error && <div className="error">{error}</div>}

      {currentJoke && (
        <JokeCard 
          joke={currentJoke} 
          onSave={handleSaveJoke}
        />
      )}
      <button 
        className="new-joke-button"
        onClick={() => fetchNewJoke()}
      >
        Obtener nuevo chiste
      </button>
    </div>
  );
}
export default App;
