import { motion } from "framer-motion";
import { useState } from "react";
import { Heart } from "lucide-react"; 

export default function FavoriteButton({ onClick }: { onClick: () => void }) {
  const [liked, setLiked] = useState(false);

  const handleClick = () => {
    setLiked(!liked);
    onClick();
  };

  return (
    <motion.button
      whileTap={{ scale: 1.2 }}
      onClick={handleClick}
      className="mt-4 p-2 rounded-full border border-gray-300 hover:bg-pink-100 transition-colors duration-300"
    >
      <motion.div
        animate={{ scale: liked ? 1.3 : 1, color: liked ? "#e11d48" : "#6b7280" }}
        transition={{ type: "spring", stiffness: 300 }}
      >
        <Heart fill={liked ? "#e11d48" : "none"} size={32} />
      </motion.div>
    </motion.button>
  );
}
